import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';

const FinalPage = () => {
  const { packageId } = useParams();
  const [packageData, setPackageData] = useState(null);
  useEffect(() => {
    const fetchPackage = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/v1/packages/${packageId}`);
        const data = await response.json();
        setPackageData(data);
      } catch (error) {
        console.error('Error fetching package:', error);
      }
    };

    fetchPackage();
  }, [packageId]);
  return (
    <div className='bg-gray-100 min-h-screen py-40 flex text-primary'>
      <div className="max-w-2xl mx-auto bg-white shadow-md p-6 w-1/2">
      <p className="text-2xl font-bold mb-6">Encomenda feita com sucesso!</p>
      <p><span className='font-bold'>Package Number:</span> {packageId}</p>
      {packageData && (
        <div>
          <p><span className='font-bold'>Your Name:</span> {packageData.userName}</p>
          <p><span className='font-bold'>Your Email:</span> {packageData.userEmail}</p>
          <p>Where to get your pack</p>
          <p><span className='font-bold'>Your Email:</span> {packageData.pickupPoint.name}</p>
          {/* Exiba outros dados da encomenda aqui */}
        </div>
      )}
      </div>

    </div>
  );
};

export default FinalPage;
