import React, {useContext} from 'react';
// import useParams
import {useParams} from 'react-router-dom';
//import cart context
import {CartContext} from '../contexts/CartContext';
// import product context
import {ProductContext} from '../contexts/ProductContext';

const Checkout = () => {
  // get product id from the url
  const {products} = useContext(ProductContext);
  const {addToCart} = useContext(CartContext);

  // destructure product
  return (
    <><div>
          <h1>Checkout</h1>
      </div>
      <div className="bg-gray-100 min-h-screen py-20">
            <div className="max-w-2xl mx-auto bg-white shadow-md p-6">
                <h1 className="text-2xl font-bold mb-6">Checkout</h1>
                <form className="space-y-4">
                    <div className="flex flex-col">
                        <label htmlFor="name" className="text-gray-700 font-medium">Name</label>
                        <input type="text" id="name" className="border border-gray-300 px-3 py-2 rounded" />
                    </div>
                    <div className="flex flex-col">
                        <label htmlFor="email" className="text-gray-700 font-medium">Email</label>
                        <input type="email" id="email" className="border border-gray-300 px-3 py-2 rounded" />
                    </div>
                    <div className="flex flex-col">
                        <label htmlFor="address" className="text-gray-700 font-medium">Address</label>
                        <input type="text" id="address" className="border border-gray-300 px-3 py-2 rounded" />
                    </div>
                    <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Place Order</button>
                </form>
            </div>
        </div></>
  );


};
export default Checkout;
