import React, {useContext, useState, useEffect} from 'react';

const Tracking = () => {
  const [packageCode, setPackageCode] = useState('');
  const [packageData, setPackageData] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`http://localhost:8080/api/v1/packages/${packageCode}`);
      const data = await response.json();
      setPackageData(data);
    } catch (error) {
      console.error('Error fetching package:', error);
    }
  };
    return(
        <div className="bg-gray-100 min-h-screen py-40 flex flex-col text-primary">
            <div className="max-w-2xl mx-auto bg-white shadow-md p-6 w-1/2">
              <h1 className="text-2xl font-bold mb-6">Track your package</h1>
              <form className="space-y-4">
                <div className="flex flex-col">
                  <label htmlFor="name" className="text-gray-700 font-medium">
                    Package Code
                  </label>
                  <input
              type="text"
              id="packageCode"
              className="border border-gray-300 px-3 py-2 rounded"
              value={packageCode}
              onChange={(event) => setPackageCode(event.target.value)}
            />
          </div>
          <button
            type="submit"
            className="bg-primary text-white px-4 py-2 rounded"

          >
            Submit
          </button>
        </form>
      </div>
      {/* Package Data */}
      {packageData && (
        <div className="max-w-2xl mx-auto bg-white shadow-md p-6 w-1/2 font-semibold">
          Package Information
          <div className="font-normal">Package Code: {packageData.packageCode}</div>
          <div className="font-normal">Pickup Point: {packageData.pickupPoint}</div>
          <div className="font-normal">Package Owner: {packageData.packageOwner}</div>
          <div className="font-normal">Package Status: {packageData.packageStatus}</div>
        </div>
      )}
    </div>
    );
};



export default Tracking;
