import React, {useContext} from 'react';

//import link
import {Link} from 'react-router-dom';

//import icons
import {BsPlus, BsEyeFill} from 'react-icons/bs';

// import cart context
import {CartContext} from '../contexts/CartContext';

const Product = ({product}) => {
  consuseContext(CartContext);
  // destructure product
  const price = '14.99';
  const {rank, book_image, title}= product;
  return(
    <div>
      <div className='border border-[#e4e4e4] h-[300px] mb-4 relative overflow-hidden group transition'>
        <div className='w-full h-full flex justify-center items-center'>
          {/* image */}
          <div className='w-[200px] mx-auto flex justify-center items-center'>
            <img className='max-h-[160px] group-hover:scale-110 transition duration-300' src={book_image} alt=""/>
          </div>
        </div>
        {/* buttons */}
        <div className='absolute top-6 -right-11 group-hover:right-5 p-2 flex flex-col items-center justify-center gap-y-2 opacity-0 group-hover:opacity-100 transition-all durantion-300'>
          <buttons>
            <div className='flex justify-center items-center text-white w-12 h-12 bg-red-500'>
              <BsPlus className='text-3xl'/>
            </div>
          </buttons>
          <Link to={`/product/${rank}`} className='w-12 h-12 bg-white flex justify-center items-center text-primary drop-shadow-x1'>
            <BsEyeFill />
          </Link>
        </div>
      </div>
      {/* title & price */}
      <div>
        <Link to={`/product/${rank}`}>
          <h2 className='font-semibold mb-1'>{title}</h2>
        </Link>
        <div className='font-semibold'>$ {price}€</div>
      </div>
    </div>
  );
};

export default Product;
