import React from 'react';
import { Link } from 'react-router-dom';
import {IoMdAdd, IoMdClose, IoMdRemove} from 'react-icons/io';

const CartItem = ({item}) => {
  // destructure item
  const {rank, title, book_image, price, amount} = item;
  return (
  <div className='flex'>
    <div className='w-full min-h-[150px] flex items-center gap-x-4'>
      {/* image */}
      <Link to={`/product/${rank}`}>
        <img className='max-w-[80px]' src={book_image} alt=""/>
      </Link>
      <div className='w-full flex flex-col'>
        {/* title & remove icon */}
        <div className='flex justify-between mb-2'>
          <Link to={`/product/${rank}`} className='text-sm uppercase font-medium max-w-[240px] text-primary hover:underline'>{title}</Link>
          {/* remove icon */}
          <div className='text-x1 cursor-pointer'>
            <IoMdClose className='text-gray-500 hover:text-red-500 transition'/>
          </div>
        </div>
        <div className='bg-pink-200 flex gap-x-2 h-[36px] text-sm'>
          {/* quantity */}
          <div className='flex flex-1 max-w-[10]'>
            {/* minus icon*/}
            <div className='flex-1'>
              <IoMdRemove />
            </div>
            {/* amount */}
            <div>{amount}</div>
            {/* plus icon*/}
            <div>
              <IoMdAdd />
            </div>
          </div>
          {/* item price */}
          <div>item price</div>
          {/* final price */}
          <div>final price</div>
        </div>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
